void test_sample_antiorthogonal(){
    unsigned char compressed_sk[PRIVATE_KEY_SEED_LENGTH_BYTES];
    randombytes(compressed_sk, PRIVATE_KEY_SEED_LENGTH_BYTES);
    SHAKE_STATE_STRUCT sk_shake_state;
    initialize_csprng(&sk_shake_state, compressed_sk, PRIVATE_KEY_SEED_LENGTH_BYTES);
    unsigned char G_seed[SEED_LENGTH_BYTES];
    csprng_randombytes(G_seed, SEED_LENGTH_BYTES, &sk_shake_state);

    FQ_ELEM A[K][K_pad];
    antiorthogonal_sample(A,G_seed);

    FQ_ELEM row_by_col;
    for(uint8_t i=0; i < K; i++){
        for(uint8_t j=0; j < K; j++){
            row_by_col = 0;
            for(uint8_t k=0; k<K; k++){
                row_by_col = fq_add(row_by_col,fq_mul(A[i][k],A[j][k]));
            }
            if(row_by_col != 0 && i!=j){
                printf("FAIL! Some row is not orthogonal!\n");
            }else if(row_by_col != 126 && i==j){
                printf("FAIL! Some row is not self-antiorthogonal\n");
            }
        }
    }
}

void test_fq_operations(){
    FQ_ELEM c[K];
    for(int i=0; i<10; i++){
        rand_range_q_elements(c,K);
        printf("Val: %i\n", c[5]);
        printf("Mult by 2: %i\n", fq_mul(c[5],2));
        printf("Adding 1: %i\n", fq_add(c[5],1));


        printf("Codeword: [");
        for(uint8_t i=0; i<K; i++){
            printf(" %i, ", c[i]);
        }
        printf("]\n");
        FQ_ELEM in_prod;
        inner_prod(&in_prod,c);
        printf("Inner prod: %i\n", in_prod);
        anti_normalize(c);
        inner_prod(&in_prod,c);
        printf("Inner prod after normalization: %i\n", in_prod);
    }
}

