void test_anti_normalize(){
    unsigned char compressed_sk[PRIVATE_KEY_SEED_LENGTH_BYTES];
    randombytes(compressed_sk, PRIVATE_KEY_SEED_LENGTH_BYTES);
    SHAKE_STATE_STRUCT sk_shake_state;
    initialize_csprng(&sk_shake_state, compressed_sk, PRIVATE_KEY_SEED_LENGTH_BYTES);
    unsigned char G_seed[SEED_LENGTH_BYTES];
    csprng_randombytes(G_seed, SEED_LENGTH_BYTES, &sk_shake_state);
    FQ_ELEM c[K];
    int counter = 0;
    int samples = 1000;
    for(int i=0; i<samples; i++){
        rand_range_q_state_elements(&sk_shake_state, c, K);
        FQ_ELEM i_prod;
        inner_prod(&i_prod,c);
        anti_normalize(c);
        inner_prod(&i_prod,c);
        if(i_prod == 126) counter++;
    }
    printf("Counter antinormalization: %i/%i\n",counter,samples); // -> 50%
}

int check_antiorthogonal(const FQ_ELEM M[K][K]){
    //Check decompressed G antiorthogonality
    FQ_ELEM row_by_col;
    for(uint8_t i=0; i < K; i++){
        for(uint8_t j=0; j < K; j++){
            row_by_col = 0;
            for(uint8_t k=0; k<K; k++){
                row_by_col = fq_add(row_by_col,fq_mul(M[i][k],M[j][k]));
            }
            if(row_by_col != 0 && i!=j){
                printf("FAIL! Some row is not orthogonal!\n");
                return 1;
            }else if(row_by_col != 126 && i==j){
                printf("FAIL! Some row is not self-antiorthogonal\n");
                return 1;
            }
        }
    }
    return 0;
}


void test_speck_keygen(){
    printf("KEYPAIRS: %i\n", NUM_KEYPAIRS);
    for(int sample=0; sample<100; sample++){
        speck_prikey_t sk;
        speck_pubkey_t pk;
        SPECK_keygen(&sk,&pk);

        // Get public G
        rref_generator_mat_t G_rref;
#ifdef SPECK_RESAMPLE_G
        generator_sample(&G_rref, pk.G_0_seed);
#endif
#ifdef SPECK_COMPRESS_G 
        expand_to_rref_speck(&G_rref,pk.G_0_rref);
#endif
        //check_antiorthogonal(G_rref.values);

        // Sample codeword from G 
        FQ_ELEM c[N] = {0};
        sample_codeword_rref(c,G_rref);

        for(int key = 0; key < NUM_KEYPAIRS-1; key++){
            // Get public GP
#ifdef SPECK_COMPRESS_GP
            rref_generator_mat_t GP_rref;
            expand_to_rref_speck(&GP_rref,pk.SF_G[key]);
#endif
            //check_antiorthogonal(GP_rref.values);

            // Permute the codeword (c -> cp)
            FQ_ELEM cp[N] = {0};
            for(int i=0; i<N; i++){
                cp[i] = c[sk.permutations[key][i]];
            }

            // Check cp belongs to GP
            // (No need to compute H for now, just use G as parity-check)
            // cp = [cp1 cp2], cp*GP.T = [cp1 cp2*A.T]
            FQ_ELEM cp2AT[K] = {0};

            for(uint8_t i=0; i<K; i++){
                cp2AT[i] = 0; 
                for(uint8_t j=0; j<K; j++){
#ifdef SPECK_COMPRESS_GP
                    cp2AT[i] = fq_add(cp2AT[i],
                            fq_mul(cp[j+K],GP_rref.values[i][j]));
#else
                    cp2AT[i] = fq_add(cp2AT[i],
                            fq_mul(cp[j+K],pk.SF_G[key][i][j]));
#endif
                }
            }

            for(uint8_t i=0; i<K; i++){
                if(cp2AT[i] != fq_sub(0,cp[i])){
                    printf("Codeword doesn't belong to code, cp2*A.T[%i] != -cp1[%i]\n",i,i);
                }
            }
        }
    } 
}

