int SPECK_sign_verify_test_multiple(void){
    unsigned long long smlen = 0, mlen1;
    unsigned char *m  = (unsigned char *)calloc(MLEN, sizeof(unsigned char));
    unsigned char *sm = (unsigned char *)calloc(MLEN+CRYPTO_BYTES, sizeof(unsigned char));
    unsigned char       pk[CRYPTO_PUBLICKEYBYTES] = {0}, sk[CRYPTO_SECRETKEYBYTES] = {0};

    int ret = 0;
    for (size_t i = 0; i < NUMBER_OF_TESTS; ++i) {
        const uint32_t msg_len = (uint32_t)rand() % MLEN;
        randombytes(m, msg_len);

        int ret_val;
        if ((ret_val = crypto_sign_keypair(pk, sk)) != 0) {
            printf("crypto_sign_keypair returned <%d>\n", ret_val);
            return -1;
        }

        fprintBstr(stdout, "pk = ", pk, CRYPTO_PUBLICKEYBYTES);
        fprintBstr(stdout, "sk = ", sk, CRYPTO_SECRETKEYBYTES);

        if ( (ret_val = crypto_sign(sm, &smlen, m, msg_len, sk, pk)) != 0) {
            printf("crypto_sign returned <%d>\n", ret_val);
            return -1;
        }
        fprintBstr(stdout, "sm = ", sm, smlen);
        if ( (ret_val = crypto_sign_open(m, &mlen1, sm, smlen, pk)) != 0) {
            printf("crypto_sign_open returned <%d>\n", ret_val);
            return -1;
        }

        if(mlen1 != msg_len) {
            printf("crypto_sign_open wrong length\n");
            return -1;
        }
        printf("OK: %zu\n", i);

        ret |= ret_val;
    }

    return ret;
}

void test_c1s_compression(){
    speck_prikey_t sk;
    speck_pubkey_t pk;
    SPECK_keygen(&sk,&pk);

    FQ_ELEM u[K];
    FQ_ELEM c1s[W][K_pad];
    for(int i=0; i<W; i++){
        rand_range_q_elements(u,K);
        for(int j=0; j<W; j++){
            c1s[i][j] = u[j];
        }
    }

    FQ_ELEM compressed_c1s[SPECK_C1S_PACKEDBYTES];
    compress_c1s(compressed_c1s,c1s);

    FQ_ELEM recovered_c1s[W][K_pad];
    expand_c1s(recovered_c1s,compressed_c1s);
    for(int i=0; i<W; i++){
        for(int j=0; j<W; j++){
            if(c1s[i][j] != recovered_c1s[i][j]){
                printf("c1s do not coincide!\n");
            }
        }
    }

}

